<style>
	.popup {
        display: none;
        position: fixed;
        z-index: 1000;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background-color: rgba(0, 0, 0, 0.5);
    }

    .popup-content {
        background-color: white;
        padding: 20px;
        border-radius: 10px;
        margin: 10% auto;
        width: 80%;
        max-width: 400px;
        box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.2);
        position: relative;
    }

    .popup-content h3 {
        margin-top: 0;
        color: #ff3600;
    }

    .popup-content label {
        display: block;
        margin: 10px 0 5px;
    }

    .popup-content input,
    .popup-content textarea {
        width: 100%;
        padding: 10px;
        margin-bottom: 10px;
        border: 1px solid #ccc;
        border-radius: 5px;
    }

    .close {
        position: absolute;
        top: 10px;
        right: 15px;
        color: #333;
        font-size: 20px;
        cursor: pointer;
    }

</style>
